import React from "react";
import {
  View,
  StyleSheet,
  Text,
  Image,
  Dimensions,
  TouchableOpacity,
} from "react-native";

const { width, height } = Dimensions.get("window");

export default CarouselItem = ({ item }) => {
  return (
    <View style={styles.cardView}>
      <Image
        style={styles.image}
        source={require("../../imgs/huracan-otto.jpg")}
      />
      <View style={styles.textView}>
        <Text style={styles.itemTitle}>#{item.id}</Text>
        <Text style={styles.itemDescription}>
          Descripcion: {item.description}
        </Text>
        <Text style={styles.itemDescription}>Estado: {item.state}</Text>
        <TouchableOpacity onPress={() => null} style={styles.button}>
          <Text style={styles.buttonText}>Ver m√°s</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  cardView: {
    flex: 1,
    width: width - 20,
    height: height / 3,
    backgroundColor: "white",
    margin: 10,
    borderRadius: 5,
    shadowColor: "#000",
    shadowOffset: { width: 0.5, height: 0.5 },
    shadowOpacity: 0.5,
    shadowRadius: 3,
    elevation: 5,
  },
  textView: {
    position: "absolute",
    bottom: 10,
    margin: 10,
    left: 5,
  },
  image: {
    width: width - 20,
    height: height / 3,
    borderRadius: 10,
  },
  itemTitle: {
    color: "white",
    fontSize: 22,
    shadowColor: "#000",
    shadowOffset: { width: 0.8, height: 0.8 },
    shadowOpacity: 1,
    shadowRadius: 3,
    marginBottom: 5,
    fontWeight: "bold",
    elevation: 5,
  },
  itemDescription: {
    color: "white",
    fontSize: 20,
    shadowColor: "#000",
    shadowOffset: { width: 0.8, height: 0.8 },
    shadowOpacity: 1,
    shadowRadius: 3,
    elevation: 5,
  },
  button: {
    backgroundColor: "#008652",
    padding: 5,
    borderWidth: 1,
    borderRadius: 10,
    borderColor: "black",
    marginRight: 150,
  },
  buttonText: {
    fontSize: 16,
    textAlign: "center",
    color: "#fff",
    fontWeight: "bold",
  },
});
